import React, { Component } from 'react';
import styled from 'styled-components';
import OutlinedTextFields from './TextField';
import FloatingActionButtons from './Button';

// eslint-disable-next-line react/prefer-stateless-function



// eslint-disable-next-line react/prefer-stateless-function
class App extends Component {
  state = [{ task: 'treinamento', id: 1 }, { task: 'reunião', id: 2 }]

  handleChange = name => (event) => {
    this.setState({
      [name]: event.target.value,
    });
  };

  render() {
    return (
      <div>
        <h1>Task Manager</h1>
        { this.state.map(elem => (
          <div className="task" key={elem.id}>
            <div className="inline">
              <OutlinedTextFields task={elem.task} onChange={this.handleChange} />
              <FloatingActionButtons />
            </div>
          </div>
        )) }
      </div>
    );
  }
}

export default App;
