import { ADD, UPDATE, SUBTRACT } from "../constants";
export function addTask() {
    return {
        type: ADD
    };
}

export function updateTask(taskList, taskId) {
    return {
        type: UPDATE,
        taskList,
        taskId,
    };
}

export function removeTask(taskList, taskId) {
    return {
        type: SUBTRACT,
        taskList,
        taskId,
    };
}
