import React, { Component } from 'react';
import styled from 'styled-components';
import OutlinedTextFields from './TextField';
import FloatingActionButtons from './Button';

// eslint-disable-next-line react/prefer-stateless-function



// eslint-disable-next-line react/prefer-stateless-function
class App extends Component {
  state = {taskList: [{ task: 'treinamento', id: 1 }, { task: 'reunião', id: 2 }]}
  handleChange = name => (task, taskId) => {
    const  { taskList } = this.state
    let targetIndex = taskList.findIndex(element => taskId === element.id);

    console.log(targetIndex)
    this.setState({
      taskList: [
        ...taskList.slice(0,targetIndex),
        {id:taskId, task},
        ...taskList.slice(targetIndex+1, taskList.length)
      ]

    })
    // this.state.taskList.findIndex(element => taskId === element.id)

    // this.state.taskList.forEach((element, index) => {
    //   if(taskId === element.id){
    //     targetIndex = index;
    //   }

    // });

  };

  render() {
    return (
      <div>
        <h1>Task Manager</h1>
        { this.state.taskList.map(elem => (
          <div className="task" key={elem.id}>
            <div className="inline">
              <OutlinedTextFields task={elem.task} taskId={elem.id} onChange={this.handleChange} />
              <FloatingActionButtons />
            </div>
          </div>
        )) }
      </div>
    );
  }
}

export default App;
