import { createStore} from "redux";
let id_increment = 1;
const reducer = (state, action) => {
  switch (action.type) {
    case "ADD":
      const taskList  = action.taskList
      state = {
        taskList: [
          ...taskList,
          {task: '', id: id_increment}
        ],
      };
      id_increment=id_increment+1;

      break;

      default:
      break;
  }
  return state;
};

const store = createStore(reducer , 1);

store.subscribe(() => {
  console.log("store:", store.getState())
});

