import React, { Component } from 'react';
import OutlinedTextFields from './components/TextField';
import ButtonTools from './components/Button';
import ButtonAdd from './components/ButtonAdd';



// eslint-disable-next-line react/prefer-stateless-function
class App extends Component {

  // state = {taskList: [{ task: 'treinamento', id: 1 }, { task: 'reuni√£o', id: 2 }]}
  state = {taskList: []}

  render() {
    return (
      <div>
        <h1>Task Manager</h1>
        <ButtonAdd addTask={this.populateList}/>

        { this.state.taskList.map(elem => (
          <div className="task" key={elem.id}>
              <OutlinedTextFields task={elem.task} taskId={elem.id} onChange={this.updateList} />
              <ButtonTools taskId={elem.id} onClick={this.removeList}/>

          </div>
        )) }
      </div>
    );
  }
}

export default App;
