import { ADD, UPDATE, SUBTRACT } from "../constants";
export function addTask() {

    return {
        type: ADD
    };
}

export function updateTask(taskId) {
    return {
        type: UPDATE,
        taskId,
    };
}

export function removeTask(taskId) {
    return {
        type: SUBTRACT,
        taskId,
    };
}
