import { ADD, UPDATE, SUBTRACT } from "../constants";
let id_increment=1;

const TaskReducer = (state, action) => {
    switch (action.type) {
      case ADD:
        const taskList = action.taskList
        state = {
          taskList: [
            ...taskList,
            {task: '', id: id_increment}
          ],
        };
        id_increment=id_increment+1;
        return state;

      case UPDATE:
        const toUpdate = action.taskList
        const task = action.task
        let targetIndex = toUpdate.findIndex(element => action.taskId === element.id);

        state = {toUpdate: [
          ...toUpdate.slice(0,targetIndex),
          {id:action.taskId, task},
          ...toUpdate.slice(targetIndex+1, toUpdate.length)
        ]}
        return state;

      case SUBTRACT:
        let tgIndex = action.taskList.findIndex(element => action.taskId === element.id);
        let tsList = action.taskList
        state = {tsList: [
          ...tsList.slice(0,tgIndex),
          ...tsList.slice(tgIndex+1, tsList.length)
        ]}

        return state;


      default:
      break;
    }
  };
  export default TaskReducer;
